mapper.update:
update goods_store

	GOODS_NAME = #{record.goodsName,jdbcType=VARCHAR}
	GOODS_NUM = #{record.goodsNum,jdbcType=INTEGER}
	EXP = #{record.exp,jdbcType=TIMESTAMP}


	GOODS_ID = #{record.goodsId,jdbcType=VARCHAR}
	and INSERT_DATE =
	#{record.insertDate,jdbcType=TIMESTAMP}

mapper.select:
select

from goods_store


		AND GOODS_ID = #{goodsStore.goodsId,jdbcType=VARCHAR}

		AND GOODS_NAME =
			#{goodsStore.goodsName,jdbcType=VARCHAR}

		AND GOODS_NUM = #{goodsStore.goodsNum,jdbcType=INTEGER}

		AND INSERT_DATE =
			#{goodsStore.insertDate,jdbcType=TIMESTAMP}

		AND EXP = #{goodsStore.exp,jdbcType=TIMESTAMP}



	order by #{sidx} #{order}

LIMIT #{start},#{end}
